          return false;\n                  }\n\n                  var consult = getValue(\'consult.value\');\n                  var ageRange = getValue(\'ageRange.value\');\n                  var moreThan12weeks = getValue(\'moreThan12weeks.value\');\n\n                  if (consult === \"6355\" &amp;&amp; (ageRange === \'\' || moreThan12weeks === \'\')) {\n                    getField(\"ageRange.error\").html(\"obrigatório\").show();\n                    getField(\"moreThan12weeks.error\").html(\"obrigatório\").show();\n                    return false;\n                  }\n\n                  var pregnantWeeks = getValue(\'pregnantWeeks.value\');\n                  if(pregnantWeeks > 42){\n                    getField(\"pregnantWeeks.error\").html(\"o valor não pode ser supererior que 42\").show();\n                    return false;\n                  }\n\n                  var weight = getValue(\'weight.value\');\n                  if(20 > weight &amp;&amp; weight != 0){\n                    getField(\"weight.error\").html(\"peso invalido (20-180 kg)\").show();\n                    return false;\n                  }\n\n                  if(weight > 180 &amp;&amp; weight != 0){\n                    getField(\"weight.error\").html(\"peso invalido (20-180 kg)\").show();\n                    return false;\n                  }\n\n                  // var lastConsult = parseInt(\"<lookup expression=\"fn.latestObs(\'6361\').valueCoded\"/>\");\n\n                  // if(consult == lastConsult){\n                  //   getField(\"consult.error\").html(\"Consulta já existente\").show();\n                  //   return false;\n                  // }\n\n                  var startDateTB = new Date(getValue(\'startDateTB.value\'));\n                  var endDateTB = new Date(getValue(\'endDateTB.value\'));\n\n                  if(startDateTB > endDateTB){\n                    getField(\"endDateTB.error\").html(\"A data de fim não pode ser inferior a de inicio\").show();\n                    return false;\n                  }\n\n                  return true;\n              });\n\n             // consult validator\n             jQuery(document).ready(function(){\n\n                $j(\"#prophilaxysNVP input\").attr(\'disabled\',false);\n                var startARV = parseInt(\"<lookup complexExpression=\"#foreach($obs in $fn.allObs(1504)) #if($obs.valueCoded == \'631\') $obs.valueCoded #end #end\"/>\");\n                \n                if(startARV === 631){\n                  $j(\"#prophilaxysNVP input\").attr(\'disabled\',true);\n                }\n\n                $j(\"#prophilaxysAZTNVP input\").attr(\'disabled\',false);\n                var nvpAzt = parseInt(\"<lookup complexExpression=\"#foreach($obs in $fn.allObs(1504)) #if($obs.valueCoded == \'1801\') $obs.valueCoded #end #end\"/>\");\n                \n                if(nvpAzt === 1801){\n                  $j(\"#prophilaxysAZTNVP input\").attr(\'disabled\',true);\n                }\n\n                getField(\'consult.value\').live(\'change\', function() {\n\n                  $j(\"#ageRange select\").attr(\'disabled\',false);\n                  $j(\"#moreThan12weeks input:radio\").attr(\'disabled\', false);\n                  $j(\"#seroStatus select\").attr(\'disabled\', false);\n\n                  $j(\"#cured input:radio\").attr(\'disabled\', false);\n                  $j(\"#lowWeight input:radio\").attr(\'disabled\', false);\n                  $j(\"#abandon input:radio\").attr(\'disabled\', false);\n                  $j(\"#prophilaxysARVFOLLOW input\").attr(\'disabled\', false);\n\n                  if(getValue(\"consult.value\") == \"6355\"){\n                    $j(\"#cured input:radio\").attr(\'disabled\', true);\n                    $j(\"#lowWeight input:radio\").attr(\'disabled\', true);\n                    $j(\"#abandon input:radio\").attr(\'disabled\', true);\n                    $j(\"#prophilaxysARVFOLLOW input\").attr(\'disabled\', true);\n                  }\n\n                  if (getValue(\'consult.value\') != \'6355\') {\n                    $j(\"#ageRange select\").attr(\'disabled\',true);\n                    $j(\"#moreThan12weeks input:radio\").attr(\'disabled\', true);\n                    $j(\"#seroStatus select\").attr(\'disabled\', true);\n                  }\n                  \n                });\n             \n                //serostatus validator\n                getField(\'seroStatus.value\').live(\'change\', function(){\n                    $j(\"#hivTestCPN select\").attr(\'disabled\', false);\n\n                    var seroStatus = getValue(\'seroStatus.value\');\n          \n                    if (seroStatus == \"703\") {\n                      $j(\"#hivTestCPN select\").attr(\'disabled\', true);\n                    }\n                });\n\n                // validate HIV status if partner present\n                $j(\"#testPartner select\").attr(\'disabled\',true);\n\n                $j(\'#partner input[type=\"radio\"]\').on(\'click change\', function(e) {                 \n                    var yesValue = $j(\'#partner input[type=\"radio\"]:checked\').val();\n\n                    if(yesValue === \'1065\'){\n                       $j(\"#testPartner select\").attr(\'disabled\',false);\n                       return;\n                    }\n                    \n                    $j(\"#testPartner select\").attr(\'disabled\',true);\n                });\n\n                //validate woman test\n                $j(\'#hivTestCPN select\').on(\'change\', function(e) {                 \n                    var value =  getValue(\'hivTestCPN.value\');\n                    console.log(value);\n                    if(value === \'664\'){\n                      $j(\"#prophilaxysCZT select\").attr(\'disabled\',true);\n                      $j(\"#prophilaxysARV select\").attr(\'disabled\',true);\n                      return;\n                    }\n\n                    $j(\"#prophilaxysCZT select\").a