n              font-size: 10pt;\n            }\n\n            .tab-data p {\n              margin: 3px;\n            }\n\n            .tab-data-I {\n              float: left;\n            }\n            .tab-data-II {\n              float: right;\n            }\n\n            .tab-data-II label {\n              width: 300px;\n            }\n\n             \n            .rd-tab label{\n             width: 60px;\n            }\n\n            .rd-tab input{\n             width: 60px;\n             margin-top: 0px;\n            }\n\n            .last-details{\n              float: none;\n              margin-top: 24%;\n            }\n\n            .rd-tab1 input{\n             width: 15px !important;\n            }\n\n            </style>\n            <script type=\"text/javascript\">\n              beforeSubmit.push(function() {\n\n                  var sifilisPartner = getValue(\'sifilisPartner.value\');\n                  var testPartner = getValue(\'testPartner.value\');\n                  var partner = getValue(\'partner.value\');     \n\n                  if ((sifilisPartner != \'\' || testPartner != \'\') &amp;&amp; partner == \'\') {\n                      getField(\'partner.error\').html(\'obrigatório\').show();\n                      return false;\n                  }\n\n                  // var lowWeight = getValue(\'lowWeight.value\');\n                  // var brachialPerimeter = getValue(\'brachialPerimeter.value\'); \n\n                  // if(lowWeight == \"1066\" || brachialPerimeter == \"6371\" || brachialPerimeter == \"6372\"){\n                  //   getField(\'cured.error\').html(\'obrigatório\').show();\n                  //   return false;\n                  // }\n\n                  var diagnosticITS = getValue(\"diagnosticITS.value\");\n                  var itsTreatment = getValue(\'itsTreatment.value\')\n\n                  if ((diagnosticITS == \"864\" || diagnosticITS == \"5993\") &amp;&amp; itsTreatment == \'\') {\n                    getField(\'itsTreatment.error\').html(\'obrigatório\').show();\n                    return false;\n                  }\n\n                  var consult = getValue(\'consult.value\');\n                  var ageRange = getValue(\'ageRange.value\');\n                  var moreThan12weeks = getValue(\'moreThan12weeks.value\');\n\n                  if (consult === \"6355\" &amp;&amp; (ageRange === \'\' || moreThan12weeks === \'\')) {\n                    getField(\"ageRange.error\").html(\"obrigatório\").show();\n                    getField(\"moreThan12weeks.error\").html(\"obrigatório\").show();\n                    return false;\n                  }\n\n                  var pregnantWeeks = getValue(\'pregnantWeeks.value\');\n                  if(pregnantWeeks > 42){\n                    getField(\"pregnantWeeks.error\").html(\"o valor não pode ser supererior que 42\").show();\n                    return false;\n                  }\n\n                  var weight = getValue(\'weight.value\');\n                  if(20 > weight &amp;&amp; weight != 0){\n                    getField(\"weight.error\").html(\"peso invalido (20-180 kg)\").show();\n                    return false;\n                  }\n\n                  if(weight > 180 &amp;&amp; weight != 0){\n                    getField(\"weight.error\").html(\"peso invalido (20-180 kg)\").show();\n                    return false;\n                  }\n\n                  // var lastConsult = parseInt(\"<lookup expression=\"fn.latestObs(\'6361\').valueCoded\"/>\");\n\n                  // if(consult == lastConsult){\n                  //   getField(\"consult.error\").html(\"Consulta já existente\").show();\n                  //   return false;\n                  // }\n\n                  var startDateTB = new Date(getValue(\'startDateTB.value\'));\n                  var endDateTB = new Date(getValue(\'endDateTB.value\'));\n\n                  if(startDateTB > endDateTB){\n                    getField(\"endDateTB.error\").html(\"A data de fim não pode ser inferior a de inicio\").show();\n                    return false;\n                  }\n\n                  return true;\n              });\n\n             // consult validator\n             jQuery(document).ready(function(){\n\n                $j(\"#prophilaxysNVP input\").attr(\'disabled\',false);\n                var startARV = parseInt(\"<lookup complexExpression=\"#foreach($obs in $fn.allObs(1504)) #if($obs.valueCoded == \'631\') $obs.valueCoded #end #end\"/>\");\n                \n                if(startARV === 631){\n                  $j(\"#prophilaxysNVP input\").attr(\'disabled\',true);\n                }\n\n                $j(\"#prophilaxysAZTNVP input\").attr(\'disabled\',false);\n                var nvpAzt = parseInt(\"<lookup complexExpression=\"#foreach($obs in $fn.allObs(1504)) #if($obs.valueCoded == \'1801\') $obs.valueCoded #end #end\"/>\");\n                \n                if(nvpAzt === 1801){\n                  $j(\"#prophilaxysAZTNVP input\").attr(\'disabled\',true);\n                }\n\n                getField(\'consult.value\').live(\'change\', function() {\n\n                  $j(\"#ageRange select\").attr(\'disabled\',false);\n                  $j(\"#moreThan12weeks input:radio\").attr(\'disabled\', false);\n                  $j(\"#seroStatus select\").attr(\'disabled\', false);\n\n                  $j(\"#cured input:radio\").attr(\'disabled\', false);\n                  $j(\"#lowWeight input:radio\").attr(\'disabled\', false);\n                  $j(\"#abandon input:radio\").attr(\'disabled\', false);\n                  $j(\"#prophilaxysARVFOLLOW input\").attr(\'disabled\', false);\n\n                  if(getValue(\"consult.value\") == \"6355\"){\n                    $j(\"#cured input:radio\").attr(\'disabled\', true);\n                    $j(\"#lowWeight input:radio\").attr(\'disabled\', true);\n                    $j(\"#abandon input:radio\").attr(\'disabled\', true);\n                    $j(\"#prophilaxysARVFOLLOW input\").attr(\'disabled\', true);\n                  }\n\n                  if (getValue(\'consult.value\') != \'6355\') {\n                    $j(\"#ageRange select\").attr(\'disabled\',true);\n                    $j(\"#moreThan12weeks input:radio\").attr(\'disabled\', true);\n                    $j(\"#seroStatus select\").attr(\'disabled\', true);\n                  }\n                  \n                });\n             \n                //serostatus validator\n                getField(\'seroStatus.value\').live(\'change\', function(){\n                    $j(\"#hivTestCPN select\").attr(\'disabled\', false);\n\n                    var seroStatus = getValue(\'seroStatus.value\');\n          \n                    if (seroStatus == \"703\") {\n                      $j(\"#hivTestCPN select\").attr(\'disabled\', true);\n                    }\n                });\n\n                // validate HIV status if partner present\n                $j(\"#testPartner select\").attr(\'disabled\',true);\n\n                $j(\'#partner input[type=\"radio\"]\').on(\'click change\', function(e) {                 \n                    var yesValue = $j(\'#partner input[type=\"radio\"]:checked\').val();\n\n                    if(yesValue === \'1065\'){\n                       $j(\"#testPartner select\").attr(\'disabled\',false);\n                       return;\n                    }\n                    \n                    $j(\"#testPartner select\").attr(\'disabled\',true);\n                });\n\n                //validate woman test\n                $j(\'#hivTestCPN select\').on(\'change\', function(e) {                 \n                    var value =  getValue(\'hivTestCPN.value\');\n                    console.log(value);\n                    if(value === \'664\'){\n                      $j(\"#prophilaxysCZT select\").attr(\'disabled\',true);\n                      $j(\"#prophilaxysARV select\").attr(\'disabled\',true);\n                      return;\n                    }\n\n                    $j(\"#prophilaxysCZT select\").attr(\'disabled\',false);\n                    $j(\"#prophilaxysARV select\").attr(\'disabled\',false);\n                });\n\n                $j(\'#sifilisTest select\').on(\'change\', function(e) {                 \n                    var value =  getValue(\'sifilisTest.value\');\n\n                    if(value === \'1228\'){\n                      $j(\"#womanSifilisTest select\").attr(\'disabled\',false);\n                      return;\n                    }\n\n                    $j(\"#womanSifilisTest select\").attr(\'disabled\',true);\n                });\n\n               $j(\'#pregnantWeeks\').change(function(){\n                  var pregnantWeeks = getValue(\'pregnantWeeks.value\');\n                  $j(\"#prophilaxysAZTNVP input\").attr(\'disabled\',false);\n\n                  if(pregnantWeeks &lt; 14){\n                     $j(\"#prophilaxysAZTNVP input\").attr(\'disabled\',true);\n                    return;\n                  }\n                });\n\n               //Validate sal ferouse\n                $j(\'#ferouseOpt input[type=\"radio\"]\').on(\'click change\', function(e) {    \n                    var yesValue = $j(\'#ferouseOpt input[type=\"radio\"]:checked\').val();\n\n                    if(yesValue === \'1066\'){\n                       $j(\"#ferouseQtd input\").attr(\'disabled\',true);\n                       return;\n                    }\n                    \n                    $j(\"#ferouseQtd input\").attr(\'disabled\',false);\n                });\n\n             });\n            </script>  \n        </head>\n\n        <body>\n        \n        <div class=\"header-box content\">\n            <h2>LIVRO DE REGISTOS DE CONSULTA PRÉ-NATAL</h2>\n          </div>\n\n        <div class=\"header content\">\n          <div class=\"header-info\">\n            <h3>REPÚBLICA DE MOÇAMBIQUE</h3>\n            <h3>SERVIÇO NACIONAL DE SAÚDE</h3>\n            <h3>MOD - SIS - B01</h3>\n            <p>\n              <label>NID/US:</label>\n            </p>\n            <p>\n              <label>NID/HDD:</label>\n              <lookup expression=\"patient.getPatientIdentifier(2)\"/>\n            </p>\n          </div>\n          <div class=\"header-data\">\n            <p>\n              <label>Nome:</label>\n              <lookup expression=\"patient.personName\"/>\n            </p>\n            <p>\n              <label>Sexo/Data de Nasc./Idade:</label>\n              <lookup expression=\"patient.gender\"/> - <lookup expression=\"patient.birthdate\"/> - <lookup expression=\"patient.age\"/>\n            </p>\n            <p>\n              <label>Nº de BI/Outra identificação:</label>\n              <lookup expression=\"patient.getPatientIdentifier(3)\"/>/<lookup expression=\"patient.getPatientIdentifier(1)\"/>\n            </p>\n            <p>\n              <label>Distrito/Cidade:</label>\n              <lookup complexExpression=\"#foreach( $addr in $patient.addresses ) $!addr.countyDistrict #end\"/>\n            </p>\n            <p>\n              <label>Localidade/Bairro:</label>\n              <lookup complexExpression=\"#foreach( $addr in $patient.addresses ) $!addr.region #end\"/>/<lookup complexExpression=\"#foreach( $addr in $patient.addresses ) $!addr.subregion #end\"/>\n            </p>\n            <p>\n              <label>Célula/Quarteirão:</label>\n              <lookup complexExpression=\"#foreach( $addr in $patient.addresses ) $!addr.neighborhoodCell #end\"/>\n            </p>\n            <p>\n              <label>Avenida/Rua/Casa:</label>\n              <lookup complexExpression=\"#foreach( $addr in $patient.addresses ) $!addr.address1 #end\"/>\n            </p>\n          </div>\n         \n          <div class=\"clear\"/>\n          <label >Pessoa de Referência:</label><br/>\n          <obsgroup groupingConceptId=\"1609\">\n            <obs conceptId=\"1441\" labelText=\"Nome:\" size=\"20\"/> \n            <obs conceptId=\"1442\" labelText=\"Apelido:\" size=\"20\"/> \n            <obs conceptId=\"1611\" labelText=\"Tel:\" size=\"15\" maxlength=\"9\"/>\n          </obsgroup>\n          <p>\n            <label>Unidade Sanitária:</label><br/>\n            \n<encounterLocation id=\"localConsulta\" default=\"5\" order=\"5\" />\n          </p>\n          <p>\n            <obs conceptId=\"a26e8692-d65c-47c7-9284-8e78eb86802f\" labelText=\"Número do Livro:\" size=\"20\"/> |==|\n            <obs conceptId=\"9fdb2c0b-e33a-42c1-9ade-6c57c702c284\" labelText=\"Número da página:\" size=\"20\" />\n          </p>\n        </div>\n         <div class=\"content\">\n            <div class=\"demo-data demo-data-partI\">\n              <p>\n                <label>Data da consulta:</label>\n                <encounterDate id=\"dataConsulta\"/>\n              </p>\n              <p>\n                <label>Consultas:</label>\n                <obs conceptId=\"6361\" id=\"consult\" answerConceptIds=\"6355,6356,6357,6358,6359,6360\" answerLabels=\"1ª,2ª,3ª,4ª,5ª,6ª\"/>\n              </p>\n              <p>\n                <label>Faixa Etária:</label>\n                <obs conceptId=\"6366\" id=\"ageRange\" answerConceptIds=\"6362,6363,6364,6365\" answerLabels=\"10-14,15-19,20-24,25+\"/>\n              </p>\n              <p>\n                <label>Data da última mestruação:</label>\n                <obs conceptId=\"e1dc0dd0-1d5f-11e0-b929-000c29ad1d07\" />\n              </p>\n            </div>\n            <div class=\"demo-data demo-data-partII\">\n              <p>\n                <label>Idade Gestacional (em semanas):</label>\n                <obs conceptId=\"1279\" size=\"10\" required=\"true\" id=\"pregnantWeeks\"/> \n              </p>\n              <p id=\"rdp\">\n                <label id=\"rdpI\"> &lt;12 semanas:</label>\n                <obs conceptId=\"6367\" id=\"moreThan12weeks\" answerConceptIds=\"1065,1066\" answerLabels=\"Sim, Não\" style=\"radio\"/>\n              </p>\n              <p id=\"rdp\">\n                <label id=\"rdpI\">Parceiro Presente ?</label>\n                <obs conceptId=\"6368\" id=\"partner\" answerConceptIds=\"1065,1066\" answerLabels=\"Sim, Não\" style=\"radio\"/>\n              </p>\n              <p>\n                <label id=\"rdpI\">Data provável do parto:</label>\n                <obs conceptId=\"e1e76284-1d5f-11e0-b929-000c29ad1d07\" size=\"10\"/>\n              </p>\n            </div>\n            <div class=\"clear\" />\n            <div class=\"tabs\">\n              <ul>\n                <li>\n                  <input type=\"radio\" checked=\"true\" name=\"tabs\" id=\"tab1\" class=\"rd-visible\" />\n                  <label for=\"tab1\">Nutrição</label>\n                  <div id=\"tab-content-I\" class=\"tab-content\">\n                    <div class=\"tab-data tab-data-I\">\n                      <p>\n                        <label>Peso(KG):</label>\n                         <obs conceptId=\"5089\" size=\"10\" id=\"weight\"/>\n                      </p>\n                      <p>\n                        <label>Perímetro Braquial:</label>\n                         <obs conceptId=\"6373\" id=\"brachialPerimeter\" answerConceptIds=\"6370,6371,6372\" answerLabels=\"˂21 cm (DAG), menor ou igual a 21cm e menor que 23 cm (DAM)s, &gt;23 cm (normal)\"/>\n                      </p>\n                    </div>\n                    <div class=\"tab-data tab-data-II\">\n                     <p >\n                        <label>Ganho ≥1.5 Kg/mês a partir 2º trimestre ?</label>\n                        <obs conceptId=\"6369\" id=\"lowWeight\" answerConceptIds=\"1065,1066\" answerLabels=\"Sim, Não\" style=\"radio\" class=\"rd-tab\"/>\n                     </p>\n                     <p>\n                        <label>Recebeu suplementos nutricionais ?</label>\n                        <obs conceptId=\"2152\" answerConceptIds=\"1065,1066\" answerLabels=\"Sim, Não\" style=\"radio\" class=\"rd-tab\"/>\n                     </p>\n                     <p>\n                        <label>Suplementos nutricionais:</label>\n                        <obs conceptId=\"e1e205f0-1d5f-11e0-b929-000c29ad1d07\" class=\"rd-tab\"/>\n                     </p>\n                      <p >\n                        <label>Recebeu desparasitante ?</label>\n                        <obs conceptId=\"6377\" answerConceptIds=\"1065,1066\" answerLabels=\"Sim, Não\" style=\"radio\" class=\"rd-tab\"/>\n                     </p>\n                     <p>\n                        <label>Sal Ferroso com Ácido Fólico?</label>\n                        <obs conceptId=\"6378\" id=\"ferouseOpt\" answerConceptIds=\"1065,1066\" answerLabels=\"Sim, Não\" style=\"radio\" class=\"rd-tab\"/>\n                     </p>\n                     <p>\n                        <label>Quantidade de Sal Ferroso</label>\n                        <obs conceptId=\"35fba30b-a7da-4f39-a7e0-2cef36a06348\" id=\"ferouseQtd\" class=\"rd-tab\"/>\n                     </p>\n                    </div>\n                    <div class=\"clear\" />\n                    <div class=\"tab-data tab-data-I\">\n                      <p>\n                        <label style=\"font-weight:bold; width:150px;\">Resultado:</label>\n                      </p>\n                      <p >\n                        <label>Curado ?</label>\n                        <obs conceptId=\"6375\" id=\"cured\" answerConceptIds=\"1065,1066\" answerLabels=\"Sim, Não\" style=\"radio\" class=\"rd-tab\"/>\n                      </p>\n                      <p>\n                        <label>Abandono ?</label>\n                        <obs conceptId=\"6376\" id=\"abandon\" answerConceptIds=\"1065,1066\" answerLabels=\"Sim, Não\" style=\"radio\" class=\"rd-tab\"/>\n                      </p>\n                    </div>\n                  </div>\n                </li>\n                <li>\n                  <input type=\"radio\" name=\"tabs\" id=\"tab2\" class=\"rd-visible\" />\n                  <label for=\"tab2\">Exames</label>\n                  <div id=\"tab-content-II\" class=\"tab-content\">\n                    <div class=\"tab-data tab-data-I\">\n                      <p>\n                        <label>Tensão Arterial (TA ≥ 140/90mmHg) ?</label>\n                        <obs conceptId=\"6379\" required=\"true\" answerConceptIds=\"1065,1066,1118\" answerLabels=\"Sim, Não, Não Fez\"/>\n                      </p>\n                      <p>\n                        <label>Hemoglobina (HGB ˂11g/dL)?</label>\n                        <obs conceptId=\"6380\" answerConceptIds=\"1065,1066,1118\" answerLabels=\"Sim, Não, Não Fez\"/>\n                      </p>\n                       <p>\n                        <label>Proteinúria ?</label>\n                        <obs conceptId=\"6381\" answerConceptIds=\"1065,1066,1118\" answerLabels=\"Sim, Não, Não Fez\"/>\n                      </p>\n                       <p>\n                        <label>Glicosúria ?</label>\n                        <obs conceptId=\"6382\" answerConceptIds=\"1065,1066,1118\" answerLabels=\"Sim, Não, Não Fez\"/>\n                      </p>\n                    </div>\n                  </div>\n                </li>\n                <li>\n                  <input type=\"radio\" name=\"tabs\" id=\"tab3\" class=\"rd-visible\"/>\n                  <label for=\"tab3\">Sífilis e outras ITS\'s</label>\n                  <div id=\"tab-content-III\" class=\"tab-content\">\n                    <div class=\"tab-data tab-data-I\">\n                      <p>\n                        <label style=\"font-weight:bold; width:150px;\">Diagnóstico de ITS</label>\n                      </p>\n                      <p>\n                        <obs conceptId=\"1080\" answerConceptId=\"864\" style=\"checkbox\" answerLabel=\"Ulceras Genitais\"/>\n                      </p>\n                      <p>\n                        <obs conceptId=\"1080\" answerConceptId=\"5993\" style=\"checkbox\" answe